import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';

// Fetch projects by workspace
export const fetchProjectsByWorkspace = createAsyncThunk(
  'project/fetchProjectsByWorkspace',
  async (workspaceId, thunkAPI) => {
    try {
      // Implement API call to fetch projects
      // const response = await projectService.getProjectsByWorkspace(workspaceId);
      // return response.data;
      return { message: `Fetched projects for workspace ${workspaceId} (mock)` };
    } catch (error) {
      return thunkAPI.rejectWithValue(error.message);
    }
  }
);

const projectSlice = createSlice({
  name: 'project',
  initialState: {
    projects: [],
    currentProject: null,
    pathwaysInProject: [],
    status: 'idle',
    error: null,
  },
  reducers: {
    setCurrentProject: (state, action) => {
      state.currentProject = action.payload;
    },
  },
  extraReducers: (builder) => {
    builder
      .addCase(fetchProjectsByWorkspace.pending, (state) => {
        state.status = 'loading';
      })
      .addCase(fetchProjectsByWorkspace.fulfilled, (state, action) => {
        state.status = 'succeeded';
        state.projects = action.payload; // Assuming payload is an array of projects
      })
      .addCase(fetchProjectsByWorkspace.rejected, (state, action) => {
        state.status = 'failed';
        state.error = action.payload;
      });
  },
});

export const { setCurrentProject } = projectSlice.actions;
export default projectSlice.reducer;
